import React, { useState, useEffect } from 'react';
import { uniqueId } from 'lodash';
import filesize from 'filesize';
import { id } from '../../../components/KidList/index'

import GlobalStyle from '../../../styles/global';
import { App, Container, Content } from './styles.js';
//import './style.css';

import { Link } from 'react-router-dom'

import api from '../../../services/api'
import Upload from '../../../components/Upload';
import FileList from '../../../components/FileList';

function addImgEmotion() {
  const [kids, setKids] = useState([]);

  useEffect(() => {
    api.get('http://localhost:3000/listKids')
    .then((response) => {
      setKids(response.data)
    });
  }, []);

  const [uploadedFile, setState] = useState([]);
  

  function handleUpload(photo) {
    const uploadedFile = photo.map(photo => ({
      photo,
      id: uniqueId(),
      name: photo.name,
      readableSize: filesize(photo.size),
      preview: URL.createObjectURL(photo),
      Progress: 0,
      uploaded: false,
      error: false,
      url: photo.url,
    }));

    setState({
      uploadedFile: uploadedFile.concat(uploadedFile)
    });

    uploadedFile.forEach(createdKid);

    console.log(photo);
  }

  function updateFile(id, data) {
    setState({ uploadedFile: this.state.uploadedFile.map(uploadedFile => { 
      return id === uploadedFile.id 
        ? { ...uploadedFile, ...data } 
        : uploadedFile;
      })
    });
  }

  /*processUpload = (uploadedFile) => {

    var data = new FormData();

    data.append('photo', uploadedFile.photo, uploadedFile.name);

    for (var key of data.entries()) {
      console.log(key[0] + ': ' + key[1]);
    }

    api.post('insertKidPhoto', data, {
      onUploadProgress: e => {
        const progress = parseInt(Math.round((e.loaded * 100) / e.total));

        this.updateFile(uploadedFile.id, {
          progress
        })
      }
    }).then(response => {
      this.updateFile(uploadedFile.id, {
        uploaded: true,
        id: response.data.id,
        ur: response.data.url
      })
    }).catch(() => {
      this.updateFile(uploadedFile.id, {
        error: true
      })
    })
  }*/

  function createdKid(uploadedFile) {

    var data = new FormData();

    data.append('image', uploadedFile.photo, uploadedFile.name);
    data.append('response1', document.getElementById('response1').value);
    data.append('response2', document.getElementById('response2').value);
    data.append('response3', document.getElementById('response3').value);
    data.append('respCorrect', document.getElementById('respCorrect').value);

    for (var key of data.entries()) {
      console.log(key[0] + ': ' + key[1]);
    }

    api.post('/kids/:kid_id/createdActivity', data, {
      onUploadProgress: e => {
        const progress = parseInt(Math.round((e.loaded * 100) / e.total));

        this.updateFile(uploadedFile.id, {
          progress,
        })
      }
    }).then(response => {
      this.updateFile(uploadedFile.id, {
        uploaded: true,
        id: response.data.id,
        ur: response.data.url
      })
    }).catch(() => {
      this.updateFile(uploadedFile.id, {
        error: true
      })
    })
  }
  
  //const { uploadedFile } = this.state;

  return (
    <App>
      <nav className="navbar navbar-light bg-light">
        <Link to="/">
            <button type="button" className="button button-info">Voltar</button>
        </Link>
        <h5 className="navbar-brand float-center">Adicionando atividade</h5>
        <h1> </h1>
      </nav>
      <Container>
        <Content>
          <form encType='multipar/form-data'>
            <div className="form-infoKid" style={{marginTop: "4%"}}>
              <div className="form-group">
                <label htmlFor="kids">Criança:</label>
                <div className="form-row">
                    <select id="kids" className="form-control mr-sm-2 col-4">
                        {kids.map((kid) => {
                            return (
                                <option selected>{kid.name}</option> 
                            )
                            })}
                    </select>
                    <Link to="/Menu">
                        <button type="submit" className="btn btn-outline-success d-inline-block" width="auto">
                            <i className="fas fa-sync"></i>
                        </button>
                    </Link>

                    <Link to="/ImagesWords">
                        <button type="submit" className="btn btn-outline-success d-inline-block" width="auto">
                            <i className="fas fa-plus"></i>
                        </button>
                    </Link>
                </div>
            </div>
              <div className="form-group" >
                <label htmlFor="emotion">Emoção</label>
                <input type="text" id="emotion" name="emotion" className="form-control" placeholder="Qual será a emoção trabalhada?" />
              </div>
              <div className="form-group" >
                <label htmlFor="image">Escolha a imagem</label>
                <Upload onUpload={handleUpload} id="image" name="image" />
                
                { !!uploadedFile.length && (
                  <FileList file={uploadedFile} />
                )}
              </div>
              
              <div className="form-group">
                  <label htmlFor="response1">Resposta 1</label>
                  <input type="text" id="response1" name="response1" className="form-control" placeholder="Emoção 1" />
              </div>  

              <div className="form-group">
                  <label htmlFor="response2">Resposta 2</label>
                  <input type="text" id="response2" name="response2" className="form-control" placeholder="Emoção 2" />
              </div>

              <div className="form-group">
                  <label htmlFor="response3">Resposta 3</label>
                  <input type="text" id="response3" name="response3" className="form-control" placeholder="Emoção 3" />
              </div>

              <div>
                <Link to="/Kids">
                  <button className="button button-danger">Cancelar</button>
                </Link>
                <button onClick ={createdKid} className="button button-success">Criar atividade</button>
              </div>
            </div>
          </form>
        </Content>
        <GlobalStyle />
      </Container>
    </App>
  )
}

export default addImgEmotion;
