import React, { useState, useEffect} from 'react';
import Axios from 'axios';

function KidSelect({ }){ 

    const [listKids, setListKids] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:3000/listKids')
        .then((response) => {
          setListKids(response.data)
        });
    },[]);
    
    return(
        <div className="form-group">
            <label htmlFor="kid_id">Crian√ßa:</label>
            
            {listKids.map((kid) => {
                return (                   
                    <select className="form-control" placeholder="Selecione">
                        <option> </option>
                        <option>{ kid.name }</option> 
                    </select>
                )
            })}
            
            
        </div>
    )
}

export default KidSelect;
