import React, { Component } from 'react';
import { uniqueId } from 'lodash';
import filesize from 'filesize';

import GlobalStyle from '../../../global/styles';
import { App, Container, Content } from './styles.js';

import { Link } from 'react-router-dom'

import api from '../../../services/api/index';
import Upload from '../../../components/Upload';
import FileList from '../../../components/mappings/FileList';

class addTask3 extends Component {

  state = {
    uploadedFilePreview: [],
    uploadedFile: undefined,

    rewardFilePreview: [],
    rewardFile: undefined,

    task: []
  };

  handleUpload = image => {
    const addedFile = image.map(image => ({
      image,
      id: uniqueId(),
      name: image.name,
      readableSize: filesize(image.size),
      preview: URL.createObjectURL(image),
      Progress: 0,
      uploaded: false,
      error: false,
      url: image.url,
    }));

    //mostra preview
    this.setState({
      uploadedFilePreview: addedFile,
      uploadedFile: addedFile[0]
    });
  }

  updateFile = (id, data) => {
    this.setState({ 
      uploadedFilePreview: this.state.uploadedFilePreview.map(uploadedFile => { 
        return id === uploadedFile.id 
          ? { ...uploadedFile, ...data } 
          : uploadedFile;
      })
    });
  }

  createdActivity = () => {
    const query = this.props.location.search;
    const kid_id = query.replace("?", "")
    console.log(query);

    var data = new FormData();

    data.append('kid_id', kid_id);
    
    data.append('name', document.getElementById('name').value);
    data.append('emotion', document.getElementById('emotion').value);
    data.append('response1', document.getElementById('response1').value);
    data.append('response2', document.getElementById('response2').value);
    data.append('response3', document.getElementById('response3').value);
    data.append('image', this.state.uploadedFile.image, this.state.uploadedFile.name);
    data.append('status', 'NÃ£o realizada');

    for (var key of data.entries()) {
      console.log(key[0] + ': ' + key[1]);
    }

    api.post(`/kids/${kid_id}/createdTask`, data)
    .then((response) => {
      this.setState({
        task: response.data.id
      });
      console.log(this.state.task);
    })
  }

  handleReward = image => {
    const addedFile = image.map(image => ({
      image,
      id: uniqueId(),
      name: image.name,
      readableSize: filesize(image.size),
      preview: URL.createObjectURL(image),
      Progress: 0,
      uploaded: false,
      error: false,
      url: image.url,
    }));

    //mostra preview
    this.setState({
      rewardFilePreview: addedFile,
      rewardFile: addedFile[0]
    });
  }

  updateReward = (id, data) => {
    this.setState({ 
      rewardFilePreview: this.state.rewardFilePreview.map(uploadedFile => { 
        return id === uploadedFile.id 
          ? { ...uploadedFile, ...data } 
          : uploadedFile;
      })
    });
  }

  createdReward = () => {

    var data = new FormData();

    data.append('task_id', this.state.task);
    
    data.append('photo', this.state.rewardFile.image, this.state.rewardFile.name);
    data.append('message', document.getElementById('rewardMess').value);

    for (var key of data.entries()) {
      console.log(key[0] + ': ' + key[1]);
    }

    api.post(`/tasks/${this.state.task}/createdReward`, data)
    .then((response) => {
      console.log(response.data)
    })
  }

  render() {
    const { uploadedFilePreview, rewardFilePreview } = this.state;
    return (
      <App>
        <nav className="navbar navbar-light bg-light">
          <Link to="/">
              <button type="button" className="button button-info">Voltar</button>
          </Link>
          <h5 className="navbar-brand float-center">Adicionando atividade</h5>
          <h1> </h1>
        </nav>
        <Container>
          <Content>
            <form encType='multipar/form-data'>
              <div className="form-infoKid" style={{marginTop: "4%"}}>
                <div className="form-group">
                  
                </div>
              </div>
              <div className="form-group" >
                <label htmlFor="name">Nome</label>
                <input type="text" id="name" name="name" className="form-control" placeholder="Nome da Atividade" />
              </div>
              <div className="form-group" >
                <label htmlFor="emotion">EmoÃ§Ã£o</label>
                <input type="text" id="emotion" name="emotion" className="form-control" placeholder="Qual serÃ¡ a emoÃ§Ã£o trabalhada?" />
              </div>
              <div className="form-group" >
                <label htmlFor="image">Escolha a imagem</label>
                <Upload onUpload={this.handleUpload} />
                
                { !!uploadedFilePreview.length && (
                  <FileList file={uploadedFilePreview} />
                )}
              </div>
              
              <div className="form-group">
                  <label htmlFor="response1">Resposta 1</label>
                  <input type="text" id="response1" name="response1" className="form-control" placeholder="EmoÃ§Ã£o 1" />
              </div>  

              <div className="form-group">
                  <label htmlFor="response2">Resposta 2</label>
                  <input type="text" id="response2" name="response2" className="form-control" placeholder="EmoÃ§Ã£o 2" />
              </div>

              <div className="form-group">
                  <label htmlFor="response3">Resposta 3</label>
                  <input type="text" id="response3" name="response3" className="form-control" placeholder="EmoÃ§Ã£o 3" />
              </div>

              <div className="form-group" >
                <label htmlFor="image">Escolha a recompensa</label>
                <Upload onUpload={this.handleReward} />
                
                { !!rewardFilePreview.length && (
                  <FileList file={rewardFilePreview} />
                )}
              </div>

              <div className="form-group">
                  <label htmlFor="rewardMess">Mensagem de recompnsa</label>
                  <input type="text" id="rewardMess" name="rewardMess" className="form-control" placeholder="Mensagem de Recompensa" />
              </div>

              <div>
                <Link to="/Kids">
                  <button className="button button-danger">Cancelar</button>
                </Link>

                <Link to="/Kids">
                  <button onClick={() => {this.createdActivity; this.createdReward}} className="button button-success">Criar atividade</button>
                </Link>
              </div>
            </form>
          </Content>
          <GlobalStyle />
        </Container>
      </App>
    )
  }
}

export default addTask3;
