import React, { Component } from 'react';
import { uniqueId } from 'lodash';
import filesize from 'filesize';

import GlobalStyle from '../../../styles/global';
import { App, Container, Content } from './styles.js';
//import './style.css';

import { Link } from 'react-router-dom'

import api from '../../../services/api'
import Upload from '../../../components/Upload';
import FileList from '../../../components/FileList';

class addImgEmotion extends Component {
  state = {
    uploadedFile: [],
  };

  handleUpload = photo => {
    const uploadedFile = photo.map(photo => ({
      photo,
      id: uniqueId(),
      name: photo.name,
      readableSize: filesize(photo.size),
      preview: URL.createObjectURL(photo),
      Progress: 0,
      uploaded: false,
      error: false,
      url: photo.url,
    }));

    this.setState({
      uploadedFile: this.state.uploadedFile.concat(uploadedFile)
    });

    uploadedFile.forEach(this.createdKid);

    console.log(photo);
  }

  updateFile = (id, data) => {
    this.setState({ uploadedFile: this.state.uploadedFile.map(uploadedFile => { 
      return id === uploadedFile.id 
        ? { ...uploadedFile, ...data } 
        : uploadedFile;
      })
    });
  }

  /*processUpload = (uploadedFile) => {

    var data = new FormData();

    data.append('photo', uploadedFile.photo, uploadedFile.name);

    for (var key of data.entries()) {
      console.log(key[0] + ': ' + key[1]);
    }

    api.post('insertKidPhoto', data, {
      onUploadProgress: e => {
        const progress = parseInt(Math.round((e.loaded * 100) / e.total));

        this.updateFile(uploadedFile.id, {
          progress
        })
      }
    }).then(response => {
      this.updateFile(uploadedFile.id, {
        uploaded: true,
        id: response.data.id,
        ur: response.data.url
      })
    }).catch(() => {
      this.updateFile(uploadedFile.id, {
        error: true
      })
    })
  }*/

  createdKid = (uploadedFile) => {

    var data = new FormData();

    data.append('photo', uploadedFile.photo, uploadedFile.name);
    data.append('treatment', document.getElementById('treatment').value);
    data.append('code', document.getElementById('code').value);
    data.append('name', document.getElementById('name').value);
    data.append('rate', document.getElementById('rate').value);
    data.append('birth', document.getElementById('birth').value);
    data.append('parent', document.getElementById('parent').value);
    data.append('note', document.getElementById('note').value);

    for (var key of data.entries()) {
      console.log(key[0] + ': ' + key[1]);
    }

    api.post('createdKid', data, {
      onUploadProgress: e => {
        const progress = parseInt(Math.round((e.loaded * 100) / e.total));

        this.updateFile(uploadedFile.id, {
          progress,
        })
      }
    }).then(response => {
      this.updateFile(uploadedFile.id, {
        uploaded: true,
        id: response.data.id,
        ur: response.data.url
      })
    }).catch(() => {
      this.updateFile(uploadedFile.id, {
        error: true
      })
    })
  }

  render() {
    const { uploadedFile } = this.state;
    return (
      <App>
        <nav className="navbar navbar-light bg-light">
          <Link to="/">
              <button type="button" className="button button-info">Voltar</button>
          </Link>
          <h5 className="navbar-brand float-center">Adicionando atividade</h5>
          <h1> </h1>
        </nav>
        <Container>
          <Content>
            <form encType='multipar/form-data'>
              <div className="form-infoKid" style={{marginTop: "4%"}}>
                <div className="form-group" >
                  <label htmlFor="emotion">Emoção</label>
                  <input type="text" id="emotion" name="emotion" className="form-control" placeholder="Qual será a emoção trabalhada?" />
                </div>
                <div className="form-group" >
                  <Upload onUpload={this.handleUpload} />
                  
                  { !!uploadedFile.length && (
                    <FileList file={uploadedFile} />
                  )}
                </div>
                
                <div>
                    <label htmlFor="word1">Emoção 1</label>
                    <input type="text" id="word1" name="word1" className="form-control" placeholder="Emoção 1" />
                </div>  

                <div>
                    <label htmlFor="word1">Emoção 2</label>
                    <input type="text" id="word2" name="word2" className="form-control" placeholder="Emoção 2" />
                </div>

                <div>
                    <label htmlFor="word1">Emoção 3</label>
                    <input type="text" id="word3" name="word3" className="form-control" placeholder="Emoção 3" />
                </div>

                <div>
                  <Link to="/Kids">
                    <button className="button button-danger">Cancelar</button>
                  </Link>
                  <button onClick ={this.createdKid} className="button button-success">Adicionar</button>
                </div>
              </div>
            </form>
          </Content>
          <GlobalStyle />
        </Container>
      </App>
    )
  }
}

export default addImgEmotion;
