import React, { Component } from "react";
import { Link } from 'react-router-dom'

import GlobalStyle from '../../../styles/global';
import { App, Container, Content } from './styles';

class TaskImgWords extends Component {


  allowDrop = event => {
    event.preventDefault();
  }

  drag = e => {
    e.dataTransfer.setData("text", e.target.id);
  }

  drop = e => {
    e.preventDefault();
    var data = e.dataTransfer.getData("text");
    e.target.appendChild(document.getElementById(data));
  }

  render() {
      return (
          <App>
              <nav class="navbar navbar-light bg-light">
                <Link to="/KidAcess">
                  <button type="button" class="button button-info">Voltar</button>
                </Link>
                <h4 class="navbar float-center">SAIR</h4>
              </nav>
          
              <Container>
                  <Content>
                    <form> 
                      <div class="form-infoKid float-center">
                        <div >
                          <img alt="Hulk" class="img float-center" 
                            onDragOver={this.allowDrop()} 
                            onDrop={this.drop()} align="center"  
                            src="https://rollingstone.uol.com.br/media/_versions/legacy/2015/img-1031213-ira-em-estado-bruto_widemd.jpg" />
                        </div>
          
                        
                        <div class="div-label" >
                          <div>
                              <label id="emotion1" name="emotion1" 
                                value="TRISTE"
                                draggable={true} 
                                onDragStart={this.drag()}
                                onDragEnd={(e)=> {window.alert('Ops, tente outra vez.') }}>TRISTE
                              </label>
                          </div>  
        
                          <div>
                            <label id="emotion2" name="emotion2" 
                              value="FELIZ" 
                              draggable={true} onDragStart={this.drag()}
                              onDragEnd={(e)=> window.alert('Ops, tente outra vez.')}>FELIZ
                            </label>
                          </div>
        
                          <div>
                            <label id="emotion3" name="emotion3" 
                              value="BRAVO"
                              draggable={true} onDragStart={this.drag()} 
                              onDragEnd={(e)=> window.alert('PARABÃ‰NS!!!')}>BRAVO
                            </label>
                          </div>
                        </div>
                      </div>
                    </form>
                  </Content>
                  <GlobalStyle />
              </Container>
          </App>                
      )
  }
}
export default TaskImgWords;
